var link=document.querySelector(".message_btn"),popup=document.querySelector(".modal-content"),close=document.querySelector(".modal-content-close"),login=popup.querySelector("[name=username]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-content-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-content-show")}),window.addEventListener("keydown",function(e){27==e.keyCode&&popup.classList.contains("modal-content-show")&&popup.classList.remove("modal-content-show")});